$(".checkbox.icheck label").hover(
    function () {
        $(".checkbox.icheck .icheckbox_square-blue").addClass("hover");
        $(".checkbox.icheck label").addClass("hover");
    },

    function () {
        $( ".checkbox.icheck .icheckbox_square-blue" ).removeClass("hover");
        $( ".checkbox.icheck label" ).removeClass("hover");
    }
);

$(":checkbox").change(function(){
    if(this.checked){
        $( ".checkbox.icheck .icheckbox_square-blue" ).addClass("checked");
        $( ".checkbox.icheck .icheckbox_square-blue" ).attr("aria-checked","true");
    }else{
        $( ".checkbox.icheck .icheckbox_square-blue" ).removeClass("checked");
        $( ".checkbox.icheck .icheckbox_square-blue" ).attr("aria-checked","false");
    }
});
